import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      newTitle: '',
      newDescription: '',
      newImage: null,
      newTime: '',
      newDate: ''
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleImageChange = this.handleImageChange.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    const { newTitle, newDescription, newImage, newTime, newDate } = this.state;
    const todo = {
      title: newTitle,
      description: newDescription,
      image: newImage,
      time: newTime,
      date: newDate
    };
    const todos = [...this.state.todos, todo];
    this.setState({ todos, newTitle: '', newDescription: '', newImage: null, newTime: '', newDate: '' });
  }

  handleRemove(index) {
    const todos = [...this.state.todos];
    todos.splice(index, 1);
    this.setState({ todos });
  }

  handleImageChange(e) {
    const file = e.target.files[0];
    if (file && (file.type === 'image/jpeg' || file.type === 'image/png')) {
      this.setState({ newImage: URL.createObjectURL(file) });
    } else {
      alert('Please upload a valid image file (JPEG or PNG)');
    }
  }

  render() {
    const { newTitle, newDescription, newImage, newTime, newDate, todos } = this.state;

    const todoItems = todos.map((todo, index) => (
      <div className="todo-item" key={index}>
        <h3>{todo.title}</h3>
        <p>{todo.description}</p>
        {todo.image && <img src={todo.image} alt="Uploaded" />}
        <p><strong>Time:</strong> {todo.time}</p>
        <p><strong>Date:</strong> {todo.date}</p>
        <button onClick={() => this.handleRemove(index)}>Remove</button>
      </div>
    ));

    return (
      <div className="App">
        <h1>Rencana Harian Saya</h1>
        <form onSubmit={this.handleSubmit}>
          <input
            type="text"
            placeholder="Judul Rencana"
            value={newTitle}
            onChange={e => this.setState({ newTitle: e.target.value })}
            required
          />
          <textarea
            placeholder="Deskripsi Rencana"
            value={newDescription}
            onChange={e => this.setState({ newDescription: e.target.value })}
            required
          />
          <label htmlFor="image">Upload Gambar (JPEG/PNG):</label>
          <input
            type="file"
            id="image"
            accept=".jpg,.png"
            onChange={this.handleImageChange}
          />
          <input
            type="text"
            placeholder="Waktu (jam:menit:detik)"
            value={newTime}
            onChange={e => this.setState({ newTime: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Tanggal (tanggal/bulan/tahun)"
            value={newDate}
            onChange={e => this.setState({ newDate: e.target.value })}
            required
          />
          <button type="submit">SAVE</button>
        </form>
        <div className="todo-list">
          {todoItems}
        </div>
      </div>
    );
  }
}

export default App;
