import React, { Component } from 'react';
import './App.css';

const TodoItem = ({ title, description, image, onRemove }) => (
  <li>
    <h3>{title}</h3>
    <p>{description}</p>
    <img src={image} alt="Uploaded" />
    <button onClick={onRemove}>Remove</button>
  </li>
);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      newTitle: '',
      newDescription: '',
      newImage: null
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleImageChange = this.handleImageChange.bind(this);
  }

  handleSubmit(e) {
    e.preventDefault();
    const { newTitle, newDescription, newImage } = this.state;
    const currentDate = new Date();
    const todo = {
      title: newTitle,
      description: newDescription,
      image: newImage,
      time: `${currentDate.getHours()}:${currentDate.getMinutes()}:${currentDate.getSeconds()}`,
      date: `${currentDate.getDate()}/${currentDate.getMonth() + 1}/${currentDate.getFullYear()}`
    };
    const todos = [...this.state.todos, todo];
    this.setState({ todos, newTitle: '', newDescription: '', newImage: null });
  }

  handleRemove(index) {
    const todos = [...this.state.todos];
    todos.splice(index, 1);
    this.setState({ todos });
  }

  handleImageChange(e) {
    const file = e.target.files[0];
    if (file && (file.type === 'image/jpeg' || file.type === 'image/png')) {
      this.setState({ newImage: URL.createObjectURL(file) });
    } else {
      alert('Please upload a valid image file (JPEG or PNG)');
    }
  }

  render() {
    const { newTitle, newDescription } = this.state;
    const todos = this.state.todos.map((todo, i) => (
      <TodoItem
        key={i}
        title={todo.title}
        description={todo.description}
        image={todo.image}
        onRemove={() => this.handleRemove(i)}
      />
    ));
    return (
      <div className="App">
        <h1>Rencana Harian Saya</h1>
        <form onSubmit={this.handleSubmit}>
          <input
            className="todo-input"
            autoComplete="off"
            type="text"
            name="newTitle"
            placeholder="Judul Rencana"
            value={newTitle}
            onChange={e => this.setState({ newTitle: e.target.value })}
          />
          <textarea
            className="todo-input"
            name="newDescription"
            placeholder="Deskripsi Rencana"
            value={newDescription}
            onChange={e => this.setState({ newDescription: e.target.value })}
          />
          <input
            className="todo-input"
            type="file"
            accept=".jpg,.png"
            onChange={this.handleImageChange}
          />
          <center><button type="submit" className="save-button">
            SAVE
          </button></center>
        </form>
        <div className="todo-content">
          <ol>{todos}</ol>
        </div>
      </div>
    );
  }
}

export default App;
