import React, { Component } from 'react';
import './App.css';

const TodoItem = ({ title, description, time, date, onRemove }) => (
  <li className="todo-item">
    <h3>{title}</h3>
    <p>{description}</p>
    <div className="todo-item-details">
      <p><strong>Time:</strong> {time}</p>
      <p><strong>Date:</strong> {date}</p>
    </div>
    <button onClick={onRemove} className="remove-button">Remove</button>
  </li>
);

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      todos: [],
      newTitle: '',
      newDescription: '',
      darkMode: false // Tambahkan state untuk mode gelap
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleRemove = this.handleRemove.bind(this);
    this.toggleDarkMode = this.toggleDarkMode.bind(this); // Tambahkan fungsi untuk mengaktifkan/nonaktifkan mode gelap
  }

  handleSubmit(e) {
    e.preventDefault();
    const { newTitle, newDescription } = this.state;
    const currentDate = new Date();
    const todo = {
      title: newTitle,
      description: newDescription,
      time: `${currentDate.getHours()}:${currentDate.getMinutes()}:${currentDate.getSeconds()}`,
      date: `${currentDate.getDate()}/${currentDate.getMonth() + 1}/${currentDate.getFullYear()}`
    };
    const todos = [...this.state.todos, todo];
    this.setState({ todos, newTitle: '', newDescription: '' });
  }

  handleRemove(index) {
    const todos = [...this.state.todos];
    todos.splice(index, 1);
    this.setState({ todos });
  }

  toggleDarkMode() {
    this.setState(prevState => ({
      darkMode: !prevState.darkMode
    }));
  }

  render() {
    const { newTitle, newDescription, todos, darkMode } = this.state; // Ambil state untuk mode gelap
    const todoItems = todos.map((todo, i) => (
      <TodoItem
        key={i}
        title={todo.title}
        description={todo.description}
        time={todo.time}
        date={todo.date}
        onRemove={() => this.handleRemove(i)}
      />
    ));

    return (
      <div className={`container ${darkMode ? 'dark-mode' : ''}`}> {/* Tambahkan kelas 'dark-mode' jika mode gelap aktif */}
        <div className="header">
          <h1>Rencana Harian Saya</h1>
          <button onClick={this.toggleDarkMode}>{darkMode ? 'Light Mode' : 'Dark Mode'}</button> {/* Tambahkan tombol untuk mengaktifkan/nonaktifkan mode gelap */}
        </div>
        <div className="content">
          <div className="form-container">
            <form onSubmit={this.handleSubmit} className="form">
              <div className="form-item">
                <input
                  className="todo-input"
                  autoComplete="off"
                  type="text"
                  name="newTitle"
                  placeholder="Judul Rencana"
                  value={newTitle}
                  onChange={e => this.setState({ newTitle: e.target.value })}
                />
              </div>
              <div className="form-item">
                <textarea
                  className="todo-input"
                  name="newDescription"
                  placeholder="Deskripsi Rencana"
                  value={newDescription}
                  onChange={e => this.setState({ newDescription: e.target.value })}
                />
              </div>
              <div className="form-item">
                <button type="submit" className="save-button">
                  SAVE
                </button>
              </div>
            </form>
          </div>
          <div className="todo-list-container">
            <div className="todo-content">
              <h2>Rencana Hari Ini</h2>
              <ol className="todo-list">{todoItems}</ol>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
